package forexample;

public class Sample02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 65;

		System.out.println(ch);
		System.out.println((int) ch);

		ch++;

		System.out.println(ch);
		System.out.println((int) ch);
		// alphabet
		for (ch = 67; ch < 123; ch++) {
			System.out.print(ch + " ");
		}
		System.out.println();
		// korean
		for (ch = 12593; ch < 12686; ch++) {
			System.out.print(ch + " ");

		}

	}

}
